import { useState } from 'react';
import CategoryButton from './CategoryButton'
import './MenuHeader.css'
import { FaPercent} from 'react-icons/fa'
import { GiMeal, GiThreeLeaves, GiFrenchFries, GiIceCreamCone, GiDrinkMe } from 'react-icons/gi'


const MenuHeader = () => {
    const [color, setColor] = useState("");

    const iconStyle = {
        color: "#ff6700",
        fontSize: "2.5em",
        transitionDuration: "0.2s"
    };

    return (
        <header className='MenuHeader'>
            <CategoryButton
                name='Deals'
                icon= {<FaPercent style={iconStyle} />}
                onMouseEnter={() => setColor("#00bcb")}
            />
            <CategoryButton
                name='Entrees'
                icon= {<GiMeal style={iconStyle}/>}
            />
            <CategoryButton
                name='Salads'
                icon= {<GiThreeLeaves style={iconStyle}/>}
            />
            <CategoryButton
                name='Sides'
                icon= {<GiFrenchFries style={iconStyle}/>}
            />
            <CategoryButton
                name='Dessert'
                icon= {<GiIceCreamCone style={iconStyle}/>}
            />
            <CategoryButton
                name='Drinks'
                icon= {<GiDrinkMe style={iconStyle}/>}
            />

        </header>
    );
};

export default MenuHeader;
