import { useState } from 'react';
import CategoryButton from './CategoryButton'
import './MenuHeader.css'
import { FaPercent} from 'react-icons/fa'
import { GiMeal, GiThreeLeaves, GiFrenchFries, GiIceCreamCone, GiDrinkMe } from 'react-icons/gi'


const MenuHeader = () => {
    const [color, setColor] = useState("#ff6700")

    const iconStyle = {
        fontSize: "2.2em",
        transitionDuration: "0.1s"
    };

    const toggleColor = (isHover) => {
        if(isHover) {
            this.setColor("#00bcb6")
        }
        else if(!isHover) {
            this.setColor("#ff6700")
        }
    };

    return (
        <header className='MenuHeader'>
            <CategoryButton
                name='Deals'
                icon= {<FaPercent style={iconStyle} color={color} />}
                onHover={toggleColor}
            />
            <CategoryButton
                name='Entrees'
                icon= {<GiMeal style={iconStyle} color={color}/>}
                onHover={toggleColor}
            />
            <CategoryButton
                name='Salads'
                icon= {<GiThreeLeaves style={iconStyle} color={color}/>}
                onHover={toggleColor}
            />
            <CategoryButton
                name='Sides'
                icon= {<GiFrenchFries style={iconStyle} color={color}/>}
                onHover={toggleColor}
            />
            <CategoryButton
                name='Dessert'
                icon= {<GiIceCreamCone style={iconStyle} color={color}/>}
            />
            <CategoryButton
                name='Drinks'
                icon= {<GiDrinkMe style={iconStyle} color={color}/>}
                onHover={toggleColor}
            />

        </header>
    );
};

export default MenuHeader;
