import { useState, useEffect } from 'react'
import { FiPercent} from 'react-icons/fi'
import { GiMeal, GiThreeLeaves, GiFrenchFries, GiIceCreamCone, GiDrinkMe } from 'react-icons/gi'
import CategoryButton from './CategoryButton'
import Items from './Items'
import './Menu.css'

const Menu = ({menuData}) => {
    if(menuData.length === 0) {
        // TODO: ERROR show 404 page
    }

    const Mains = menuData.filter((item)=>{return item.Category === "Mains"})
    const Drinks = menuData.filter((item)=>{return item.Category === "Drinks"})

    const [isVisible, setVisible] = useState(true)

    useEffect(() => {
        const testing =  () => {
          console.log("I'm from Menu.js")
        }

        testing()
    }, [])

    handleClick = () => {
        // e.preventDefault()
        console.log('this is ' + this)
        setVisible(!isVisible)
    }

    return (
        <div>
            <header className='MenuHeader'>
                <CategoryButton
                    onClick={this.handleClick()}
                    name='Mains'
                    icon= {<GiMeal />}
                />
                <CategoryButton
                    name='Salads'
                    icon= {<GiThreeLeaves />}
                />
                <CategoryButton
                    name='Sides'
                    icon= {<GiFrenchFries />}
                />
                <CategoryButton
                    name='Dessert'
                    icon= {<GiIceCreamCone />}
                />
                <CategoryButton
                    name='Drinks'
                    icon= {<GiDrinkMe />}
                />
            </header>
            <div className='menu-wrapper'>
                <div className='menu-container'>
                    {isVisible && <Items items={Mains} />}
                </div>
            </div>
        </div>
    );
};

export default Menu;
