    return (
        <div className='card-container'>
            <h1>{item.name}</h1>
            <p>{item.desc}</p>
            <div className='price-wrapper'>$ {item.price}</div>
            <div className="btn-wrapper"><Button text="ADD" onClick={ () => handleClick(item,1) }></Button></div>
        </div>

    );
};

export default Item
