import Button from './Button'
import './Item.css'

const Item = ({ item }) => {
    return (
        <div className='card-container'>
            <h1>{item.name}</h1>
            <p>{item.desc}</p>

            <div className='card-footer'>
                <h3>${item.price}<span><Button text="ADD"></Button></span></h3>

            </div>

        </div>

    );
};

export default Item
