import { useState, useEffect } from 'react'
import { FiPercent} from 'react-icons/fi'
import { GiMeal, GiThreeLeaves, GiFrenchFries, GiIceCreamCone, GiDrinkMe } from 'react-icons/gi'
import CategoryButton from './CategoryButton'
import Items from './Items'
import './Menu.css'

const MenuHeader = () => {
    return (
        <header className='MenuHeader'>
            <CategoryButton
                name='Deals'
                icon= {<FiPercent />}
            />
            <CategoryButton
                name='Mains'
                icon= {<GiMeal />}
            />
            <CategoryButton
                name='Salads'
                icon= {<GiThreeLeaves />}
            />
            <CategoryButton
                name='Sides'
                icon= {<GiFrenchFries />}
            />
            <CategoryButton
                name='Dessert'
                icon= {<GiIceCreamCone />}
            />
            <CategoryButton
                name='Drinks'
                icon= {<GiDrinkMe />}
            />
        </header>
    );
};

const Menu = ({menuData}) => {
    const [category, setCategory] = useState()
    let mains
    let drinks
    if(menuData.length === 2) {
        mains = menuData[0]
        drinks = menuData[1]
    } else {
        mains = []
    }

    return (

        <div>
            <MenuHeader />
            <div className='menu-wrapper'>
                <div className='menu-container'>
                    <Items items={category} />
                </div>
            </div>
        </div>
    );
};

export default Menu;
