import { useState } from 'react'
import Button from './Button'
import './Item.css'

const Item = ({ item, addToCart }) => {
    const [quantity, setQuantity] = useState(1)

    const handleClick = (item,quantity) => {
        addToCart(item,quantity)
        // console.log(item)
    }

    const btnStyle = {
        backgroundColor: "red",
        color: "white",
        fontSize: "15px",
        paddingTop: "15px",
        paddingBottom: "15px",
        paddingRight: "25px",
        paddingLeft: "25px",
        borderRadius: "10%"
    }

    // useEffect(() => {
    //     console.log('Hello from Item')
    // })

    return (
        <div className='card-container'>
            <h1>{item.name}</h1>
            <p>{item.desc}</p>
            <div className='price-wrapper'>$ {item.price}</div>
            <div className='btn-wrapper'><Button text="ADD" onClick={ () => handleClick(item,1) }></Button></div>
            <div className='quanity-btn-wrapper'>
                <button className='dec-btn'>-</button>
                <input type='text'></input>
                <button className='inc-btn'>+</button> 
            </div>
        </div>

    );
};

export default Item
