import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'

const Item = ({ item }) => {
    return (
        <div className='container'>
            {/* <h1>{item.name}</h1>
            <p>{item.desc}</p>
            <p>{item.price}</p> */}
            <Card style={{ width: '18rem' }}>
                <Card.Body>
                    <Card.Title>{item.name}</Card.Title>
                    <Card.Text>
                    {item.desc}
                    </Card.Text>
                    <Button variant="primary">ADD</Button>
                </Card.Body>
            </Card>
        </div>

    );
};

export default Item
