import { useState, useEffect } from 'react'
import { FiPercent} from 'react-icons/fi'
import { GiMeal, GiThreeLeaves, GiFrenchFries, GiIceCreamCone, GiDrinkMe } from 'react-icons/gi'
import CategoryButton from './CategoryButton'
import Items from './Items'
import './Menu.css'

const Menu = ({menuData}) => {
    if(menuData.length === 0) {
        // TODO: ERROR show 404 page
    }

    const Mains = menuData.filter((item)=>{return item.Category === "Mains"})
    const Drinks = menuData.filter((item)=>{return item.Category === "Drinks"})

    const [isVisible, setVisible] = useState([true,false])

    useEffect(() => {
        const testing =  () => {
          console.log("I'm from Menu.js")
          console.log(isVisible)
          console.log(isVisible.map(element => !element )) 
        }

        testing()
    }, [])

    const handleClick = (index) => {
        if(isVisible[index] == true) {
            return
        }

        setVisible(isVisible.map(element => !element))
        console.log('click')
    }

    return (
        <div>
            <header className='MenuHeader'>
                <CategoryButton
                    onClick={handleClick(0)}
                    name='Mains'
                    icon= {<GiMeal />}
                />
                <CategoryButton
                    name='Salads'
                    icon= {<GiThreeLeaves />}
                />
                <CategoryButton
                    name='Sides'
                    icon= {<GiFrenchFries />}
                />
                <CategoryButton
                    name='Dessert'
                    icon= {<GiIceCreamCone />}
                />
                <CategoryButton
                    onClick={handleClick(1)}
                    name='Drinks'
                    icon= {<GiDrinkMe />}
                />
            </header>
            <div className='menu-wrapper'>
                <div className='menu-container'>
                    {isVisible[0] && <Items items={Mains} />}
                    {isVisible[1] && <Items items={Drinks} />}
                </div>
            </div>
        </div>
    );
};

export default Menu;
