import { useState, useEffect } from 'react'
import { FiPercent} from 'react-icons/fi'
import { GiMeal, GiThreeLeaves, GiFrenchFries, GiIceCreamCone, GiDrinkMe } from 'react-icons/gi'
import CategoryButton from './CategoryButton'
import Items from './Items'
import './Menu.css'

const Menu = ({menuData}) => {
    let mains
    let drinks
    if(menuData.length === 2) {
        mains = menuData[0]
        drinks = menuData[1]
    } else {
        mains = []
    }
    const [category, setCategory] = useState(mains)

    const changeMenu = (menuCategory) => {
        setCategory(menuCategory)
    }

    return (
        <div>
            <header className='MenuHeader'>
                <CategoryButton
                    name='Mains'
                    icon= {<GiMeal />}
                    onClick = {changeMenu(mains)}
                />
                <CategoryButton
                    name='Salads'
                    icon= {<GiThreeLeaves />}
                />
                <CategoryButton
                    name='Sides'
                    icon= {<GiFrenchFries />}
                />
                <CategoryButton
                    name='Dessert'
                    icon= {<GiIceCreamCone />}
                />
                <CategoryButton
                    name='Drinks'
                    icon= {<GiDrinkMe />}
                    onClick= {changeMenu(drinks)}
                />
            </header>
            <div className='menu-wrapper'>
                <div className='menu-container'>
                    <Items items={category} />
                </div>
            </div>
        </div>
    );
};

export default Menu;
